chID = "#NewsGroup";\r\n                    $(SwichID).find("#chk" + CheckIndex).addClass("cur");\r\n                    $(SwichID).find("#shk" + CheckIndex).show();\r\n                </script>\r\n', '', '', 'inherit', 'closed', 'closed', '', '439-revision-v1', '', '', '2016-09-13 19:44:41', '2016-09-13 16:44:41', '', 439, 'http://demo3.saitobaza.ru/?p=440', 0, 'revision', '', 0),
