(534, 1, '2016-09-14 16:40:57', '2016-09-14 13:40:57', '\r\n                    <p style="color: #5e6263; line-height: 25px; text-indent: 2em;">\r\n                     </p><p align="center">\r\n	<span style="font-size:32px;"><strong>我们的团队</strong></span> \r\n</p>\r\n<p>\r\n	<span style="color:#4c33e5;"> 湖南中俄商贸中心为深化湖南与俄语区域国家的经济与文化交流，拥有湖南省最专业、最资深、最年轻的俄语业务团队。我们的团队全部拥有中南大学、湖南师范大学等重点大学本科以上文凭；团队成员具备工程机械、建材、家具、工业品等多个领域的实操经验；团队成员具备至少3年以上俄语从业经历；队员平均年龄仅为28岁。</span> \r\n</p>\r\n<p>\r\n	<br>\r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>因为合作，商惠湘俄！</strong></span> \r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>因为专业，我们更加专注；</strong></span> \r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>专业成就精英，专注缔造共赢；</strong></span> \r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>服务湘俄，我们不忘初心。</strong></span> \r\n</p>\r\n<p align="center">\r\n	<br>\r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>因为资深，我们敢于担当；</strong></span> \r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>资深造就经典，担当成就未来；</strong></span> \r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>服务湘俄，我们当仁不让。</strong></span> \r\n</p>\r\n<p align="center">\r\n	<br>\r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>因为年轻，我们敢于追梦；</strong></span> \r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>年轻不惧风雨，追梦星夜兼程；</strong></span> \r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>服务湘俄，我们永不止步。</strong></span> \r\n</p>\r\n<p align="center">\r\n	<br>\r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>因为有梦，我们结识彼此；</strong></span> \r\n</p>\r\n<p align="center">\r\n	<span style="font-size:24px;color:#003399;"><strong>因为相识，我们精诚合作；</strong></span> \r\n</p>\r\n<p>\r\n	<br>\r\n</p><p></p>\r\n', 'Наша команда', '', 'publish', 'open', 'open', '', 'nasha-komanda', '', '', '2016-09-14 16:40:57', '2016-09-14 13:40:57', '', 0, 'http://demo3.saitobaza.ru/?p=534', 0, 'post', '', 0),
